{
  "type": "object",
  "properties": {
    "emailBody": {
      "type": "string",
      "description": "The body content of the email to be sent"
    },
    "eventName": {
      "type": "string",
      "description": "The name of the event. (if just sending availabilities, you can leave this empty)"
    },
    "startTime": {
      "type": "string",
      "description": "The start time of the event, in ISO 8601 format.(if just sending availabilities, you can leave this empty)"
    },
    "endTime": {
      "type": "string",
      "description": "The end time of the event, in ISO 8601 format. (if just sending availabilities, you can leave this empty)"
    },
    "bookCall": {
      "type": "boolean",
      "description": "True if booking a call for a specific time slot.False if sending availabilities"
  },
  "calendarAvailabilities": {
      "type": "string",
      "description": "Detailed calendar availabilities for time as specified by manager."
    }
  },
  "required": [
    "emailBody",
    "eventName",
    "startTime",
    "endTime",
    "bookCall"
  ]
}

